import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'; // Import FormsModule
import { CurrencyConverterPipe } from './currency-converter/currency-converter.pipe'; // Import your custom pipe

import { AppComponent } from './app.component';
import { CurrencyConverterComponent } from './currency-converter/currency-converter.component';

@NgModule({
  declarations: [AppComponent, CurrencyConverterComponent, CurrencyConverterPipe], // Add the custom pipe here
  imports: [BrowserModule, FormsModule], // Include FormsModule in the imports
  providers: [],
  bootstrap: [AppComponent],
})
export class AppModule {}
